import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        itemReviews:[],
          activeItem:{
            id:null,
            product:'',
            customer:'',
            heading:'',
            rating:'',
            comment:'',
            scraped_on:'',
        },
        editing:false,
      }
      this.fetchReviews = this.fetchReviews.bind(this)
  };

  componentWillMount() {}

  fetchReviews(){
    console.log('Fetching...')
    fetch('http://localhost/api/loptop')
    .then(response => response.json())
    .then(data =>
      this.setState({
        itemReviews:data
      })
    )
  }

  render(){
    var reviews = this.state.itemReviews
      return (
        <div className="container">
          <form className="form-wrapper">
          <input className="form-control center-column" type="text" placeholder="Search a product" name="search_string"/>
          <input className="btn btn-primary center-item" type="submit" placeholder="Search"/>
          </form>

          <div className="review-wrapper">
            {reviews.map(function(review, index){
                return(
                    <div key={index} className="review-wrapper flexwrapper">
                        <span>{review.title}</span>
                    </div>
                )
            })}
          </div>
        </div>
      )
    }
  }

  export default App
