import React from 'react';
import './App.css';

const API = 'http://127.0.0.1:8000/api/';
const DEFAULT_QUERY = 'iphone7';


class App extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        itemReviews:[],
//        search_string:'',
//          activeItem:{
//            id:null,
//            product:'',
//            customer:'',
//            heading:'',
//            rating:0,
//            comment:'',
//        },
      }
      this.fetchReviews = this.fetchReviews.bind(this)
  };

  componentDidMount() {
    this.fetchReviews();
  }

  fetchReviews(){
    console.log('Fetching...yes')
    fetch(API + DEFAULT_QUERY)
    .then(response => response.json())
    .then(data =>
      this.setState({
        itemReviews:data
      })
    )
  }

  render(){
    var reviews = this.state.itemReviews
      return (
        <div className="container">
          <form className="form-wrapper" method="post">
          <input className="form-control" type="text" placeholder="Search a product" name="search_string"/>
          <input className="btn btn-primary center-item" type="submit" placeholder="Search"/>
          </form>

          <div className="flex-wrapper"><span style={{flex: 1}}>Id</span><span style={{flex: 1}}>Product</span>
            <span style={{flex: 1}}>Customer</span><span style={{flex: 1}}>Rating</span><span style={{flex: 1}}>Comment</span></div>

          <div className="review-wrapper">
            {reviews.map(function(review, index){
                return(
                    <div key={index} className="review-wrapper flex-wrapper">
                        <div style={{flex: 1}}>
                            <span>{review.id}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.product}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.customer}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.rating}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.heading}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.comment}</span>
                        </div>
                    </div>
                )
            })}
          </div>
        </div>
      )
    }
  }

  export default App
