import React from 'react';
import './App.css';

const API = 'https://127.0.0.1:8000/scrap/';


class App extends React.Component {
  constructor(props){
    super(props);
      this.state = {
        itemReviews:[],
        search_string:'iphone7',
          activeItem:{
            id:null,
            product:'',
            customer:'',
            heading:'',
            rating:0,
            comment:'',
        },
      }
      this.fetchReviews = this.fetchReviews.bind(this)
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
  };

  componentDidMount() {
    this.fetchReviews();
  }

  fetchReviews(){
    console.log('Fetching...yes')
    fetch(API + this.setState.search_string)
    .then(response => response.json())
    .then(data =>
      this.setState({
        itemReviews:data
      })
    )
  }

  handleChange(e){
    var value = e.target.value
    console.log(value)

    this.setState({
    search_string:value
    })
  }

  handleSubmit(e){
    e.preventDefault()
    console.log('ITEM', this.state.activeItem)
  }

  render(){
    var reviews = this.state.itemReviews
      return (
        <div className="container">
          <form onSubmit={this.handleSubmit} className="form-wrapper">
          <input onChange={this.handleChange} className="form-control" type="text" placeholder="Search a product" name="search_string"/>
          <input className="btn btn-primary center-item" type="submit" placeholder="Search"/>
          </form>

          <div className="flex-wrapper"><span style={{flex: 1, color:'white'}}>Id</span><span style={{flex: 1, color:'white'}}>Product</span>
            <span style={{flex: 1, color:'white'}}>Customer</span><span style={{flex: 1, color:'white'}}>Rating</span>
            <span style={{flex: 1, color:'white'}}>Comment</span></div>

          <div className="review-wrapper">
            {reviews.map(function(review, index){
                return(
                    <div key={index} className="review-wrapper flex-wrapper">
                        <div style={{flex: 1}}>
                            <span>{review.id}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.product}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.customer}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.rating}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.heading}</span>
                        </div>
                        <div style={{flex: 1}}>
                            <span>{review.comment}</span>
                        </div>
                    </div>
                )
            })}
          </div>
        </div>
      )
    }
  }

  export default App
